{
    "program": "Austin Live Music Fund",
    "response_count": 28,
    "themes": [
        {
            "theme": "Accessibility and Barriers",
            "sentiment": "negative",
            "frequency": 15,
            "key_points": [
                "Cost of tickets or admission fees",
                "Transportation and parking issues",
                "Location - Lack of nearby venues or events",
                "Limited opportunities for emerging artists"
            ],
            "recommendation": "Implement subsidy programs for ticket prices and improve transportation options to increase accessibility. Consider hosting events in diverse neighborhoods to reach more communities."
        },
        {
            "theme": "Funding for Artists",
            "sentiment": "negative",
            "frequency": 12,
            "key_points": [
                "Lack of funding for artists and creatives",
                "Rising costs of venues and performance spaces",
                "Impact of gentrification on creative spaces"
            ],
            "recommendation": "Increase funding allocations specifically for artists and creatives. Partner with local businesses and organizations to provide affordable venues and address gentrification impacts."
        },
        {
            "theme": "Diversity and Inclusion",
            "sentiment": "neutral",
            "frequency": 8,
            "key_points": [
                "Limited diversity/representation/inclusion in events",
                "Lack of awareness about events and programs",
                "Need for expanded genres such as hip hop and Latin music"
            ],
            "recommendation": "Develop outreach programs to increase diversity and representation in events. Promote a wider variety of music genres and ensure marketing efforts reach underrepresented communities."
        }
    ]
}
