{
  "program": "Thrive",
  "response_count": 590,
  "themes": [
    {
      "theme": "Accessibility Issues",
      "sentiment": "negative",
      "frequency": 40,
      "key_points": [
        "High cost of tickets or admission fees",
        "Lack of nearby venues or events",
        "Transportation and parking issues",
        "Limited diversity and representation in events"
      ],
      "recommendation": "Implement sliding scale ticket pricing, increase the number of events in underserved areas, improve public transportation options to event venues, and ensure diverse programming to attract a wider audience."
    },
    {
      "theme": "Funding and Support for Artists",
      "sentiment": "negative",
      "frequency": 30,
      "key_points": [
        "Lack of funding for artists and creatives",
        "Rising costs of venues and performance spaces",
        "Limited opportunities for emerging artists"
      ],
      "recommendation": "Increase funding opportunities for artists, subsidize venue costs, and create more platforms for emerging artists to showcase their work."
    },
    {
      "theme": "Awareness and Engagement",
      "sentiment": "neutral",
      "frequency": 25,
      "key_points": [
        "Lack of awareness about events and programs",
        "Events not matching community interests",
        "Need for expanded cultural programming"
      ],
      "recommendation": "Enhance marketing efforts to raise awareness of events, conduct community surveys to better align events with local interests, and diversify programming to include a wider array of cultural activities."
    }
  ]
}
