{
  "program": "Economic Development Department",
  "analysis": {
    "strengths_and_successes": [
      {
        "statement": "Austin's arts community has relied on EDD Cultural Arts funding for over 30 years.",
        "insight": "The program has a long-standing history of supporting the arts community, indicating a foundational role in funding operations and programming."
      }
    ],
    "areas_needing_improvement": [
      {
        "issue": "Funding instability and shifting priorities",
        "evidence": "Stabilize cultural arts funding to ensure that organizations have foundational and capacity building funds for operations and programming support. Austin's arts community has relied on EDD Cultural Arts fundings for over 30 years and in the last 5 years, the value shifts and changes in funding matrix ..."
      },
      {
        "issue": "Perceived inequity and favoritism in funding allocation",
        "evidence": "The City of Austin's Economic Development Department, and the general 'those who have more, get more' which is a common problem in funding. ... There has long been a sense that EDD grants staff played favorites ... and conversely, retaliated against its critics ... This has created an atmosphere of fear and distrust."
      },
      {
        "issue": "Panelist and review process concerns",
        "evidence": "I would like to express my deep concern regarding the review process and the panel members of the Cultural Funding Team ... The current review process ... demonstrates a lack of expertise and transparency."
      },
      {
        "issue": "Perceived exclusionary practices and social engineering",
        "evidence": "Please just institute widely accepted best practices for public funding of the arts. It's only been difficult because EDD chose to center its own social engineering ideas, rather than proven systems."
      },
      {
        "issue": "Inadequate response to community needs and venue loss",
        "evidence": "With over $12m+ to invest annually ... it's incredible that the City has been unable to stem the loss of venues and exodus of arts and culture professionals."
      },
      {
        "issue": "Over-targeting of marginalized communities to the detriment of the broader arts community",
        "evidence": "Over the past five years, funding has been targeted exclusively to marginalized communities, as defined by EDD. This has devastated the Austin arts community."
      }
    ],
    "specific_requests_or_suggestions": [
      {
        "request": "Allow lived experience to be expressed narratively in applications but not as an exclusionary eligibility threshold."
      },
      {
        "request": "Recruit panelists with arts expertise and lived experience."
      },
      {
        "request": "Write new guidelines for panelist recruitment and evaluation."
      },
      {
        "request": "Increase prioritization of organizations with proven, deep community impact over new organizations."
      },
      {
        "request": "Empower existing community groups financially rather than duplicating services."
      },
      {
        "request": "Adopt widely accepted best practices for public arts funding instead of experimental approaches."
      },
      {
        "request": "Collaborate with and empower organizations through serious partnerships, not top-down control."
      }
    ],
    "impact_statements_from_beneficiaries": [
      {
        "statement": "The damage to the professional arts sector was real. Governments don't need to reinvent the wheel.",
        "type": "Negative impact"
      },
      {
        "statement": "This has devastated the Austin arts community.",
        "type": "Negative impact"
      },
      {
        "statement": "This has created an atmosphere of fear and distrust.",
        "type": "Negative impact"
      }
    ],
    "accessibility_or_barrier_issues": [
      {
        "issue": "Exclusionary eligibility thresholds based on lived experience",
        "evidence": "Find ways to have lived experience expressed narratively in the application ... but not as an exclusionary eligibility threshold as it was in the pilot."
      },
      {
        "issue": "Lack of transparency and expertise in review process",
        "evidence": "The current review process ... demonstrates a lack of expertise and transparency."
      }
    ]
  }
}
