  "program": "Elevate",
  "response_count": 611,
  "themes": [
    {
      "theme": "Accessibility Issues",
      "sentiment": "negative",
      "frequency": 45,
      "key_points": [
        "High cost of tickets or admission fees",
        "Lack of nearby venues or events in neighborhoods",
        "Transportation and parking issues",
        "Limited diversity and representation in events"
      ],
      "recommendation": "Consider implementing tiered pricing or discounts to make events more affordable. Increase the number of events in underserved areas and improve public transportation options to venues. Enhance diversity in programming to attract a broader audience."
    },
    {
      "theme": "Funding for Artists",
      "sentiment": "negative",
      "frequency": 30,
      "key_points": [
        "Lack of funding for artists and creatives",
        "Rising costs of venues and performance spaces",
        "Limited opportunities for emerging artists"
      ],
      "recommendation": "Increase funding opportunities and grants for artists, especially emerging ones. Collaborate with local businesses and sponsors to subsidize venue costs and create more affordable spaces for performances."
    },
    {
      "theme": "Awareness and Promotion",
      "sentiment": "neutral",
      "frequency": 25,
      "key_points": [
        "Lack of awareness about events and programs",
        "Limited information reaching potential audiences",
        "Need for expanded cultural programming"
      ],
      "recommendation": "Enhance marketing efforts through social media and community outreach to raise awareness about events. Develop partnerships with local media and community organizations to spread information more effectively."
    }
  ]
}
