{
  "program": "Art in Public Places",
  "response_count": 610,
  "themes": [
    {
      "theme": "Accessibility Issues",
      "sentiment": "negative",
      "frequency": 45,
      "key_points": [
        "Cost of tickets or admission fees",
        "Transportation/parking issues",
        "Lack of nearby venues or events in neighborhoods"
      ],
      "recommendation": "Implement sliding scale ticket pricing and improve public transportation options to venues. Consider hosting events in more diverse locations to increase accessibility."
    },
    {
      "theme": "Lack of Awareness",
      "sentiment": "negative",
      "frequency": 32,
      "key_points": [
        "Limited awareness about events and programs",
        "Events do not match community interests",
        "Need for better marketing and outreach"
      ],
      "recommendation": "Enhance marketing efforts through social media, local partnerships, and community engagement to increase awareness and tailor events to community interests."
    },
    {
      "theme": "Funding and Support for Artists",
      "sentiment": "neutral",
      "frequency": 28,
      "key_points": [
        "Lack of funding for artists and creatives",
        "Rising costs of venues and performance spaces",
        "Limited opportunities for emerging artists"
      ],
      "recommendation": "Increase funding opportunities and grants for artists, and explore partnerships to reduce venue costs. Develop programs specifically for emerging artists to showcase their work."
    }
  ]
}
